package com.class7;

public class PostIncrementAndPostDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x=10;
int y=x++; //LOGIC: first we assign Y with value of X, then we increment X by 1


//PostIncrement 

int a=299;
int b=a++;
System.out.println(a);//300
System.out.println(b);//299 (we assign B with value of A, then increment A

 

//PostDecrement

int c=100;
int d=c--;
System.out.println(c);//99
System.out.println(d);//100 (we assign D with value of C, then decrement C by 1


	}

}
